
set echo on
spool CreateDS35_Tablespaces.log

--Currently this template assumes need for only single datafile per table
--This might impact performance for larger database sizes, so either user needs to edit the generated script from this template or change logic in perl script to generate required build table space script
--Paramters that need to be changed acc to database size are - number of datafiles per table, initial size of data file and size of increments for data file in case of overflow

--Paths for windows should be like this : c:\oracledbfiles\
--paths for linux should be like this : /oracledbfiles/

CREATE TABLESPACE "CUSTTBS" LOGGING DATAFILE '{CUST1DATAFILE_PATH}cust_1.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED  EXTENT MANAGEMENT LOCAL SEGMENT SPACE MANAGEMENT AUTO ;
ALTER TABLESPACE "CUSTTBS" ADD DATAFILE '{CUST2DATAFILE_PATH}cust_2.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ; 
ALTER TABLESPACE "CUSTTBS" ADD DATAFILE '{CUST2DATAFILE_PATH}cust_3.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ; 
ALTER TABLESPACE "CUSTTBS" ADD DATAFILE '{CUST2DATAFILE_PATH}cust_4.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ; 
CREATE TABLESPACE "INDXTBS" LOGGING DATAFILE '{IND1DATAFILE_PATH}indx_1.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED  EXTENT MANAGEMENT LOCAL SEGMENT SPACE MANAGEMENT AUTO;
ALTER TABLESPACE "INDXTBS" ADD DATAFILE '{IND2DATAFILE_PATH}indx_2.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ; 
ALTER TABLESPACE "INDXTBS" ADD DATAFILE '{IND2DATAFILE_PATH}indx_3.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ;
ALTER TABLESPACE "INDXTBS" ADD DATAFILE '{IND2DATAFILE_PATH}indx_4.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ;
CREATE TABLESPACE "DS_MISC" LOGGING DATAFILE '{DS_MISCDATAFILE_PATH}ds_misc.dbf' SIZE 500M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED EXTENT MANAGEMENT LOCAL SEGMENT SPACE MANAGEMENT AUTO;
CREATE TABLESPACE "ORDERTBS" LOGGING DATAFILE '{ORDER1DATAFILE_PATH}order_1.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED EXTENT MANAGEMENT LOCAL SEGMENT SPACE MANAGEMENT AUTO; 
ALTER TABLESPACE "ORDERTBS" ADD DATAFILE '{ORDER2DATAFILE_PATH}order_2.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ; 
ALTER TABLESPACE "ORDERTBS" ADD DATAFILE '{ORDER2DATAFILE_PATH}order_3.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ;
ALTER TABLESPACE "ORDERTBS" ADD DATAFILE '{ORDER2DATAFILE_PATH}order_4.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ;
CREATE TABLESPACE "MEMBERTBS" LOGGING DATAFILE '{MEMBERDATAFILE_PATH}member.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED EXTENT MANAGEMENT LOCAL SEGMENT SPACE MANAGEMENT AUTO;
CREATE TABLESPACE "REVIEWTBS" LOGGING DATAFILE '{REVIEW1DATAFILE_PATH}review_1.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED EXTENT MANAGEMENT LOCAL SEGMENT SPACE MANAGEMENT AUTO;
ALTER TABLESPACE "REVIEWTBS" ADD DATAFILE '{REVIEW2DATAFILE_PATH}review_2.dbf' SIZE 1000M REUSE AUTOEXTEND ON NEXT 100M MAXSIZE UNLIMITED ;
spool off
exit;
